pragma solidity ^0.4.2;

contract Numa {
  address public owner;
  uint public price;

  struct Message {
    uint id;
    address sender;
    bytes32 datFolderId;
    bytes32 datFileId;
  }

  event MessageCreated(
    uint id,
    address sender,
    bytes32 datFolderId,
    bytes32 datFileId
  );

  Message[] public messages;

  function Numa(address _owner) public {
    owner = _owner;
  }

  modifier onlyOwner {
    require(msg.sender == owner);
    _;
  }

  function setPrice(uint newPrice) public onlyOwner {
    price = newPrice;
  }

  function messagesLength() public view returns (uint) {
    return messages.length;
  }

  function createMessage(bytes32 datFolderId, bytes32 datFileId) public payable returns (uint) {
    require(msg.value >= price);
    messages.length++;
    uint index = messages.length - 1;

    messages[index].datFolderId = datFolderId;
    messages[index].datFileId = datFileId;
    messages[index].sender = msg.sender;
    messages[index].id = messages.length;

    MessageCreated(messages[index].id, msg.sender, datFolderId, datFileId);
  }

}
